    public List<Book> getBooksByCategory(String category) {
        SQLiteDatabase database = getReadableDatabase();
        Cursor cursor = database.rawQuery("select b.* from " + BOOK_TABLE_NAME + " b, "
                + CATEGORY_TABLE_NAME + " c where c.id = b.category and c.name = ? order by b.rating", new String[]{category});
        ArrayList<Book> books = new ArrayList<Book>();
        if (cursor.moveToFirst()) {
            do {
                books.add(new Book(cursor.getInt(0), cursor.getString(1), cursor.getString(12), cursor.getString(3),
                        cursor.getString(4), cursor.getString(5), cursor.getString(11), cursor.getString(10),
                        cursor.getInt(6), cursor.getInt(9), cursor.getString(2), cursor.getString(8), category));
            } while (cursor.moveToNext());
        }
        database.close();
        return books;
    }

    public List<Book> getBooksByWishlist(String wishlist) {
        SQLiteDatabase database = getReadableDatabase();
        Cursor cursor = database.rawQuery("select b.*, c.name from book b, wishlist w, wishlist_book wb, category c " +
                "where w.name = ? and w.id = wb.wishlist and b.id = wb.book and b.category = c.id order by b.rating", new String[]{wishlist});
        ArrayList<Book> books = new ArrayList<Book>();
        if (cursor.moveToFirst()) {
            do {
                books.add(new Book(cursor.getInt(0), cursor.getString(1), cursor.getString(12), cursor.getString(3),
                        cursor.getString(4), cursor.getString(5), cursor.getString(11), cursor.getString(10),
                        cursor.getInt(6), cursor.getInt(9), cursor.getString(2), cursor.getString(8), cursor.getString(13)));
            } while (cursor.moveToNext());
        }
        database.close();
        return books;
    }

    public List<Book> getBooksByKeyword(String search) {
        SQLiteDatabase database = getReadableDatabase();
        Cursor cursor = database.rawQuery("select b.*, c.name from book b, category c where b.category = c.id and (b.title like '%" + search + "%' or b.authors like '%" + search + "%')", null);
        ArrayList<Book> books = new ArrayList<Book>();
        if (cursor.moveToFirst()) {
            do {
                books.add(new Book(cursor.getInt(0), cursor.getString(1), cursor.getString(12), cursor.getString(3),
                        cursor.getString(4), cursor.getString(5), cursor.getString(11), cursor.getString(10),
                        cursor.getInt(6), cursor.getInt(9), cursor.getString(2), cursor.getString(8), cursor.getString(13)));
            } while (cursor.moveToNext());
        }
        database.close();
        return books;
    }

