    public Integer getWishListId(String name) {
        SQLiteDatabase database = getReadableDatabase();
        Cursor cursor = database.rawQuery("select id from " + WISH_LIST_TABLE_NAME + " where name = ?", new String[]{name});
        if (cursor.getCount() == 0) return null;
        cursor.moveToFirst();
        int id = cursor.getInt(0);
        database.close();
        return id;
    }

    public void insertWishList(String wishListName) {
        SQLiteDatabase database = getWritableDatabase();
        ContentValues values = new ContentValues();
        values.put("name", wishListName);
        database.insert(WISH_LIST_TABLE_NAME, "name", values);
    }

    public void insertWishListMapping(int wishlistId, int bookId) {
        SQLiteDatabase database = getWritableDatabase();
        ContentValues values = new ContentValues();
        values.put("book", bookId);
        values.put("wishlist", wishlistId);
        database.insert(WISHLIST_BOOK_TABLE_NAME, "name", values);
    }

    public void remvoeWishListMapping(int wishlistId, int bookId) {
        SQLiteDatabase database = getWritableDatabase();
        database.delete(WISHLIST_BOOK_TABLE_NAME, "wishlist = ? and book = ?", new String[]{String.valueOf(wishlistId), String.valueOf(bookId)});
    }

